mapper.insert:
INSERT INTO departure (
    departureName, departureCities, departureTown, departureDetailedAddress,
    departureManager, departureManagerPhoneNum, etc, regDate, userCompany
) VALUES (
    #{departureName}, #{departureCities},
    #{departureTown}
    NULL,
    #{departureDetailedAddress}
    NULL,
    #{departureManager}
    NULL,
    #{departureManagerPhoneNum}
    NULL,
    #{etc}
    NULL,
    NOW(), #{userCompany}
)

mapper.select:
SELECT * FROM departure
userCompany = #{userCompany}
ORDER BY departureID DESC

mapper.update:
UPDATE departure
arrivalName = #{arrivalName}, arrivalCities = #{arrivalCities}, arrivalTown = #{arrivalTown}, arrivalDetailedAddress = #{arrivalDetailedAddress}, arrivalManager = #{arrivalManager}, arrivalManagerPhoneNum = #{arrivalManagerPhoneNum}, etc = #{etc},
WHERE arrivalName = #{arrivalName}

mapper.select:
SELECT COUNT(*)
FROM departure
WHERE departureName = #{departureName}
