mapper.select:
SELECT * FROM cargoorder WHERE 1=1
AND orderDate BETWEEN #{startDate} AND #{endDate}
AND refNumber = #{refNumber}
AND userName LIKE CONCAT('%', #{userName}, '%')
AND departureName LIKE CONCAT('%', #{departureName}, '%')
AND arrivalName LIKE CONCAT('%', #{arrivalName}, '%')
AND arrivalCities LIKE CONCAT('%', #{arrivalCities}, '%')
AND orderNumber LIKE CONCAT('%', #{orderNumber}, '%')
ORDER BY orderID DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT orderNumber, DATE_FORMAT(orderDate, '%Y-%m-%d %H:%i') AS orderDate, -- ✅ 초 제거하고 시:분까지만 표시 DATE_FORMAT(endDate, '%Y-%m-%d %H:%i') AS endDate, refNumber, departureName, departureCities, departureTown, arrivalName, arrivalCities, arrivalTown, carWeight, kindOfCar, upDown, carNumber, driverName, driverPhoneNum, basicFare + addFare AS totalFare, userName, regDate, option1, option2, option3, option4, userCompany FROM cargoorder WHERE 1=1
AND endDate BETWEEN CONCAT(#{endDate}, ' 00:00:00') AND CONCAT(#{endDate2}, ' 23:59:59')
AND refNumber = #{refNumber}
AND (userName LIKE CONCAT('%', #{userName}, '%') OR #{userName} IS NULL)
AND (departureName LIKE CONCAT('%', #{departureName}, '%') OR #{departureName} IS NULL)
AND (departureCities LIKE CONCAT('%', #{departureCities}, '%') OR #{departureCities} IS NULL)
AND (arrivalName LIKE CONCAT('%', #{arrivalName}, '%') OR #{arrivalName} IS NULL)
AND (arrivalCities LIKE CONCAT('%', #{arrivalCities}, '%') OR #{arrivalCities} IS NULL)
AND (CAST(orderNumber AS CHAR) LIKE CONCAT('%', #{orderNumber}, '%') OR #{orderNumber} IS NULL)
AND (userCompany LIKE CONCAT('%', #{userCompany}, '%') OR #{userCompany} IS NULL)
AND userName = #{userName}
AND userCompany = #{userCompany}
ORDER BY orderID DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM cargoorder WHERE 1=1
AND orderDate BETWEEN #{startDate} AND #{endDate}
AND refNumber = #{refNumber}
AND userName LIKE CONCAT('%', #{userName}, '%')
AND departureName LIKE CONCAT('%', #{departureName}, '%')
AND arrivalName LIKE CONCAT('%', #{arrivalName}, '%')
AND arrivalCities LIKE CONCAT('%', #{arrivalCities}, '%')
AND orderNumber LIKE CONCAT('%', #{orderNumber}, '%')
AND userName = #{userName}
AND userCompany = (SELECT userCompany FROM user WHERE userName = #{userName})

SELECT * FROM cargoorder
WHERE 1=1

    AND refNumber = #{refNumber}


    AND userName LIKE CONCAT('%', #{userName}, '%')


    AND departureName LIKE CONCAT('%', #{departureName}, '%')


    AND arrivalName LIKE CONCAT('%', #{arrivalName}, '%')


    AND arrivalCities LIKE CONCAT('%', #{arrivalCities}, '%')


    AND orderNumber = #{orderNumber}

ORDER BY orderID DESC
LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE cargoorder
kindOfCar = #{kindOfCar}, userName = #{userName}, orderDate = #{orderDate}, carWeight = #{carWeight}, refNumber = #{refNumber}, userPhoneNumber = #{userPhoneNumber}, fixedCarNumber = #{fixedCarNumber}, upDown = #{upDown}, item = #{item}, etc = #{etc}, startDate = #{startDate}, endDate = #{endDate}, departureName = #{departureName}, arrivalName = #{arrivalName}, departureCities = #{departureCities}, arrivalCities = #{arrivalCities}, departureTown = #{departureTown}, arrivalTown = #{arrivalTown}, departureDetailedAddress = #{departureDetailedAddress}, arrivalDetailedAddress = #{arrivalDetailedAddress}, departureManager = #{departureManager}, arrivalManager = #{arrivalManager}, departureManagerPhoneNum = #{departureManagerPhoneNum}, arrivalManagerPhoneNum = #{arrivalManagerPhoneNum}, carNumber = #{carNumber}, driverName = #{driverName}, driverPhoneNum = #{driverPhoneNum}, basicFare = #{basicFare}, addFare = #{addFare}, option1 = #{option1}, option2 = #{option2}, option3 = #{option3}, option4 = #{option4}, destinationAddress = #{destinationAddress}
WHERE orderNumber = #{orderNumber}
